INSERT INTO `wft_investor` VALUES ('73', '96', '1', '2', '0', '1', '15630949826', '牛民\n', null, null, '2', '郑州宇通', '郑州宇通', '140100,170100,190100,200100', 'IT/互联网&nbsp;建筑/房地产&nbsp;金融理财&nbsp;娱乐休闲', '<p>\n	1．利用市场的愚蠢，进行有规律的投资。\n</p>\n<p>\n	2．买价决定报酬率的高低，即使是长线投资也是如此。\n</p>\n<p>\n	3．利润的复合增长与交易费用和税负的避免使投资人受益无穷。\n</p>\n<p>\n	4．不在意一家公司来年可赚多少，仅有意未来5至10年能赚多少。\n</p>\n<p>\n	5．只投资未来收益确定性高的企业。\n</p>\n<p>\n	6．通货膨胀是投资者的最大敌人。\n</p>\n<p>\n	7．价值型与成长型的投资理念是相通的；价值是一项投资未来现金流量的折现值；而成长只是用来决定价', '50000', '0', '10000', '100000', 'thumb_20141118151103_892.jpg', 'thumb_20141118151121_508.jpg', 'thumb_20141118151126_727.jpg', null, null, '', '', '上海市', '300000', '上海市', '310000', '黄浦区', '310100', null, null, null, null, null, null, '0');
