INSERT INTO `wft_project` VALUES ('353', '1317', '医疗界', '马旭峰', '13803857396', '医疗行业垂直电子商务平台', '医疗器械垂直电子商务平台，整合医疗产品厂家、医疗产品代理商、产品租赁金融机构、产品维修等多环节资源，构架医疗设备一站式采购平台。', '1、目前医疗设备行业电子商务平台暂时处于蓝海，投资回报率较高\n2、国家政策、医改政策推动互联网+医疗器械的发展\n3、平台搭建、平台推广、团队组建等在前期需要一定的资金支持\n4、我们的目标是达到医疗行业最具影响力的电子商务平台', '', '院方资源整合力度不够，任何投资都有风险。', '1', '410000', '河南省', '410100', '郑州市', '410101', '金水区', '140100', '互联网', '140104', '电商', '20150912160908_945.png', '', '2015-09-12 04:09:36', '0000-00-00 00:00:00', '0', '', '1', '', '', '0', '', '', '0', '2015-09-14 02:41:10');
